import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';
import './Card.css';

function Card(conteudo, onDelete) {
  const [isVisible, setIsVisible] = useState(false);

  const handleClick = () => {
    setIsVisible(!isVisible);
  };

  const handleDelete = () => {
    onDelete(conteudo.id); // Chama onDelete passando o ID do Card
  }

  const cardAnimation = useSpring({
    transform: isVisible ? 'scale(1.2)' : 'scale(1)',
  });

  return (
    <div className="card" onClick={handleClick}>
      <h2>{conteudo.title}</h2>
      <animated.div className="card-content" style={cardAnimation}>
        {isVisible ? (
          <div className="card-text">
            <p>{conteudo.content}</p>
            <button onClick={handleDelete}>Excluir</button>
          </div>
        ) : null}
      </animated.div>
    </div>
  );
}

export default Card;
