import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';
import './Card.css';

function Card(props) {
  const [isExpanded, setIsExpanded] = useState(false);

  const cardAnim = useSpring({
    height: isExpanded ? '200px' : '100px', // Define a altura do card de acordo com o estado de expans達o
  });

  return (
    <animated.div
      className="card"
      style={{ ...cardAnim, backgroundColor: '#f5f5f5' }}
      onClick={() => setIsExpanded(!isExpanded)} // Inverte o estado de expans達o ao clicar no card
    >
      <h3>{props.title}</h3>
      <p>{props.content}</p>
    </animated.div>
  );
}

export default Card;
