import React, { useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import Card from './Card';


const supabase = createClient('https://mykanhamhzdazpiimhbf.supabase.co', 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Im15a2FuaGFtaHpkYXpwaWltaGJmIiwicm9sZSI6ImFub24iLCJpYXQiOjE2ODE4MTQzMTgsImV4cCI6MTk5NzM5MDMxOH0.JEczcrpmt5XMwuPJOTM9SOzXl6D-3AETjQ3KBOliWv0');

function App() {
  const [materias, setMaterias] = useState([]);

  useEffect(() => {
    async function fetchMaterias() {
      const { data: materias, error } = await supabase.from('materias').select('*');
      if (error) console.log('Erro ao buscar matérias:', error);
      else setMaterias(materias);
    }
    fetchMaterias();
  }, []);

  const handleNovaMateriaSubmit = async (novaMateria) => {
    const { data: materia, error } = await supabase.from('materias').insert(novaMateria);
    if (error) console.log('Erro ao adicionar matéria:', error);
    else setMaterias([...materias, materia]);
  };

  const handleDelete = async (id) => {
    const { error } = await supabase.from('materias').delete().match({ id });
    if (error) console.log('Erro ao excluir matéria:', error);
    else setMaterias(materias.filter((materia) => materia.id !== id));
  };

  return (
    <div className="card-container">
      {materias.map((materia) => (
        <Card key={materia.id} title={materia.titulo} content={materia.conteudo} onDelete={() => handleDelete(materia.id)} />
      ))}
      <CardForm onSubmit={handleNovaMateriaSubmit} />
    </div>
  );
}

export default App;
