import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';
import './Card.css';

function Card(props) {
  const { title, content, onDelete } = props;

  return (
    <div className="card">
      <h2>{title}</h2>
      <div className="card-content">
        <p>{content}</p>
        <button onClick={onDelete}>Excluir</button>
      </div>
    </div>
  );
}

function CardForm(props) {
  const [titulo, setTitulo] = useState('');
  const [conteudo, setConteudo] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    if (!titulo || !conteudo) return;
    props.onSubmit({ titulo, conteudo });
    setTitulo('');
    setConteudo('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Título" value={titulo} onChange={(event) => setTitulo(event.target.value)} />
      <textarea placeholder="Conteúdo" value={conteudo} onChange={(event) => setConteudo(event.target.value)} />
      <button type="submit">Adicionar</button>
    </form>
  );
}


export default Card;
