import React, { useEffect, useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import Card from './Card';

const supabase = createClient('https://mykanhamhzdazpiimhbf.supabase.co', 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Im15a2FuaGFtaHpkYXpwaWltaGJmIiwicm9sZSI6ImFub24iLCJpYXQiOjE2ODE4MTQzMTgsImV4cCI6MTk5NzM5MDMxOH0.JEczcrpmt5XMwuPJOTM9SOzXl6D-3AETjQ3KBOliWv0');

function App() {
  const [materias, setMaterias] = useState([]);
  const [novaMateria, setNovaMateria] = useState({ titulo: '', conteudo: '' });

  useEffect(() => {
    async function fetchMaterias() {
      // faz a consulta à tabela "materias" do Supabase
      const { data, error } = await supabase.from('materias').select('*');

      if (error) {
        console.log('Erro ao buscar as matérias:', error.message);
      } else {
        setMaterias(data);
      }
    }

    fetchMaterias();
  }, []);

  return (
    <div className="card-container">
      {materias.map(materia => (
        <Card title={materia.titulo} content={materia.conteudo} key={materia.id} />
      ))}
      <form onSubmit={handleNovaMateriaSubmit}>
        <input
          type="text"
          name="titulo"
          placeholder="Título"
          value={novaMateria.titulo}
          onChange={handleNovaMateriaChange}
        />
        <textarea
          name="conteudo"
          placeholder="Conteúdo"
          value={novaMateria.conteudo}
          onChange={handleNovaMateriaChange}
        />
        <button type="submit">Adicionar matéria</button>
      </form>
    </div>

  );
}

export default App;
